package com.ch2;

public class variable1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
          String a; //SMITH
          //초기화 ( 대입연산자 )
         //왼쪽(변수)=오른쪽
          a="SMITH";
         System.out.println("SMITH");
               //상수사용
         System.out.println(a);
                            //변수사용
         System.out.println(a);                           
         // 클래스 /변수/동사
         System.out.println(a); //a "SMITH" "KING"                           
         // 클래스 /변수/동사         
         a="KING";
         System.out.println(a);
	}

}

